        U&'/*\000a * Copyright 2024 Robert A. James\000a *\000a * Licensed under the Apache License, Version 2.0 (the "License");\000a * you may not use this file except in compliance with the License.\000a * You may obtain a copy of the License at\000a *\000a *   http://www.apache.org/licenses/LICENSE-2.0\000a *\000a * Unless required by applicable law or agreed to in writing, software\000a * distributed under the License is distributed on an "AS IS" BASIS,\000a * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.\000a * See the License for the specific language governing permissions and\000a * limitations under the License.\000a */\000a\000apackage primitives_classes.compiler\000a//    : ?full 12 = if ." It''s full " else ." NOT full " then ." Finished! " ;\000a//    : ?full 12 = if ." It''s full " then ;\000a\000aimport com.rajames.forth.compiler.AbstractCompilerDirective\000aimport com.rajames.forth.compiler.CompilerDirective\000aimport com.rajames.forth.compiler.ForthCompiler\000aimport com.rajames.forth.compiler.ForthCompilerException\000aimport com.rajames.forth.dictionary.Word\000aimport com.rajames.forth.runtime.ForthInterpreter\000aimport org.apache.logging.log4j.LogManager\000aimport org.apache.logging.log4j.Logger\000a\000aclass IfC extends AbstractCompilerDirective {\000a\000a    private static final Logger log = LogManager.getLogger(this.class.getName())\000a\000a    ForthCompiler compiler\000a    ForthInterpreter interpreter\000a    @Override\000a    Boolean execute(Word newWord, ForthCompiler compiler, ForthInterpreter interpreter) {\000a        this.compiler = compiler\000a        this.interpreter = interpreter\000a\000a        // Fast Fail\000a        if (interpreter.words.stream().noneMatch(w -> w.getName().equals("then"))) {\000a            interpreter.words.clear()\000a            interpreter.nonWords.clear()\000a            interpreter\000a            throw new ForthCompilerException("No matching ''THEN for ''IF")\000a        }\000a        runup()\000a\000a        while (!this.interpreter.tokensCopy.isEmpty()) {\000a            String token = this.interpreter.tokensCopy.remove()\000a            Word thenWord = this.compiler.wordService.findByName("then")\000a            Word elseWord = this.compiler.wordService.findByName("else")\000a            if (token == thenWord.name || token == elseWord.name) {\000a                if(token == thenWord.name) {\000a                    this.compiler.forthWordsBuffer.add(thenWord.name)\000a                    this.interpreter.words.remove()\000a                    break\000a                }\000a                if(token == elseWord.name) {\000a                    def classLoader = new GroovyClassLoader()\000a                    Class groovyClass = classLoader.parseClass(elseWord.compileClass)\000a                    CompilerDirective compileTime = groovyClass.getDeclaredConstructor().newInstance() as CompilerDirective\000a                    def output = compileTime.execute(this.compiler.newWord, this.compiler, this.interpreter)\000a                    break\000a                }\000a            } else {\000a                Word word = this.compiler.wordService.findByName(token)\000a                try {\000a                    this.interpreter.words.remove()\000a                } catch(Exception ignored) {\000a                    break\000a                }\000a\000a                if (word) {\000a                    this.compiler.forthWordsBuffer.add(word.name)\000a                    if(word.compileClass) {\000a                        def classLoader = new GroovyClassLoader()\000a                        Class groovyClass = classLoader.parseClass(word.compileClass)\000a                        CompilerDirective compileTime = groovyClass.getDeclaredConstructor().newInstance() as CompilerDirective\000a                        def output = compileTime.execute(this.compiler.newWord, this.compiler, this.interpreter)\000a                    }\000a                }\000a            }\000a        }\000a        return false\000a    }\000a\000a    private void runup() {\000a        // remove all tokens from the tokensCopy up to "if"\000a        while (!this.interpreter.tokensCopy.isEmpty()) {\000a            String token = this.interpreter.tokensCopy.remove()\000a            Word ifWord = this.compiler.wordService.findByName("if")\000a            if (ifWord.name == token) {\000a                // add "if" to the list of forthWordsBuffer\000a                this.compiler.forthWordsBuffer.add(ifWord.name)\000a                break\000a            }\000a        }\000a    }\000a\000a    private Word compileLiteral(String token) {\000a        Word wordLiteral = new Word()\000a        String uniqueId = UUID.randomUUID().toString().replaceAll("-", "")\000a        try {\000a            // Integer literals\000a            Integer i = Integer.parseInt(token)\000a            wordLiteral.name = "int_${this.compiler.literal.name}_${uniqueId}"\000a            wordLiteral.stackValue = i\000a        } catch (NumberFormatException ignored) {\000a            // string literal\000a            wordLiteral.name = "str_${this.compiler.literal.name}_${uniqueId}"\000a            wordLiteral.stringLiteral = token\000a        }\000a        wordLiteral.runtimeClass = this.compiler.literal.runtimeClass\000a        wordLiteral.compileOnly = true\000a        wordLiteral.dictionary = this.compiler.dictionary\000a        wordLiteral.parentWord = this.compiler.newWord\000a        this.compiler.wordService.save(wordLiteral)\000a        return wordLiteral\000a    }\000a}\000a',
